def main():
    # Get input
    W, a, b = map(int, input().split())
    # If a and b are equal, no movement is needed
    if a == b:
        print(0)
    # If the distance between a and b is less than the width of the rectangle,
    # then movement is needed
    elif abs(a-b) < W:
        print(0)
    # Otherwise, the distance between the two rectangles should be printed
    else:
        print(abs(a-b)-W)

if __name__ == '__main__':
    main()


def main():
    # Get input and convert to int
    W, a, b = map(int, input().split()) 
    # Print the distance between the two rectangles
    print(max(0, abs(a-b)-W))

if __name__ == '__main__':
    main()

import sys

def main():
    w, a, b = map(int, sys.stdin.readline().split())
    print(max(0, b - (a + w)) if b > a + w else max(0, a - (b + w)))

if __name__ == "__main__":
    main()


