import math

def get_nth_string(n, k, first_a_in_first_pos=True):

    if n == 3:
        if k == 1:
            return "abb"
        else:
            return "bab"

    # Calculate the number of strings with the first 'a' in the first position
    # if the first 'a' is in the first position.
    num_with_first_a_in_first_pos = (n-2) * (n-3) // 2 if first_a_in_first_pos else 0

    if k <= num_with_first_a_in_first_pos:
        # The first 'a' is in the first position.
        return "a" + get_nth_string(n-1, k, first_a_in_first_pos=True)

    # The first 'a' is not in the first position.
    return "b" + get_nth_string(n-1, k-num_with_first_a_in_first_pos, first_a_in_first_pos=False)


