    days.sort()
    intervals = []
    for day in days:
        if len(intervals) == 0:
            intervals.append(day)
        else:
            if intervals[-1][1] >= day[0]:
                intervals[-1][1] = max(intervals[-1][1], day[1])
            else:
                intervals.append(day)
    days_served = 0
    for start, end in intervals:
        days_served += end - start + 1
    print(days_served)

