import sys
import math

def main():
    K = int(sys.stdin.readline().strip())
    clerks = [int(sys.stdin.readline().strip()) for i in range(K)]
    desk_positions = {desk: i for i, desk in enumerate(clerks)}
    min_passes = 0
    for i in range(K):
        min_passes = max(min_passes, desk_positions[clerks[i]] - i)
    print(min_passes)

if __name__ == '__main__':
    main()

import sys

def main():
	k = int(sys.stdin.readline())
	desks = [int(sys.stdin.readline()) for _ in range(k)]
	current_desk = 1
	passes = 0
	for desk in desks:
		if desk > current_desk:
			passes = passes + (desk - current_desk)
		current_desk = desk
	print(passes)

if __name__ == '__main__':
	main()

def main():
    K = int(input())
    desks = list(map(int, input().split()))
    # print(desks)
    pos = 0
    passes = 0
    while pos < K:
        for i in range(pos, K):
            if desks[i] == i+1:
                pos += 1
        passes += 1
    print(passes)

if __name__ == '__main__':
    main()
