import sys

def main():
    line = sys.stdin.readline()
    line = line.strip()
    print(line[0], end="")
    for i in range(len(line)):
        if line[i] == "-":
            print(line[i+1], end="")

if __name__ == "__main__":
    main()

def main():
    """Main function"""
    input_string = input()
    print("".join([input_string[i] for i in range(len(input_string)) if input_string[i].isupper()]))

if __name__ == "__main__":
    main()

def main():
    name = input()
    print(name[0] + "".join([x[0] for x in name.split("-")[1:]]))

if __name__ == "__main__":
    main()
