def main():
    days = int(input())
    temp = [int(i) for i in input().split()]
    m = max(temp)
    for i in range(days-2):
        t = max(temp[i:i+3])
        if t < m:
            m = t
            d = i+1
    print(d, m)

if __name__ == '__main__':
    main()

def main():
	n = int(input())
	temps = list(map(int, input().split()))

	max_temp = 0
	day = 0

	for i in range(n - 2):
		temp = max(temps[i], temps[i + 1], temps[i + 2])

		if temp > max_temp:
			max_temp = temp
			day = i + 1

	print(day, max_temp)

if __name__ == "__main__":
	main()

def main():
    n = int(input())
    t = list(map(int, input().split()))
    max_temp = 100
    max_day = 0
    for i in range(n-2):
        if max(t[i], t[i+2]) < max_temp:
            max_temp = max(t[i], t[i+2])
            max_day = i+1
    print(max_day, max_temp)

if __name__ == "__main__":
    main()
