def main():
    n, a = map(int, input().split())  # n: 個数, a: 容量
    v = list(map(int, input().split()))  # v: 価値のリスト
    w = list(map(int, input().split()))  # w: 重さのリスト
    vw = [(v[i], w[i]) for i in range(n)]  # 価値と重さのタプルのリスト
    vw.sort(key=lambda x: x[0] / x[1], reverse=True)  # 価値密度の昇順にソート
