    if i % 2 == 0:
        return chr((ord(c) - ord('A') + ord(key[i]) - ord('A')) % 26 + ord('A'))
    else:
        return chr((ord(c) - ord('A') - (ord(key[i]) - ord('A'))) % 26 + ord('A'))

