    a, b = [0] * n, [0] * n
    for i in range(n):
        a[i], b[i] = map(int, input().split())
    if solve(n, r, a, b):
        print('YES')
    else:
        print('NO')

def solve(n, r, a, b):
    for i in range(n):
        r += b[i]
        if r < a[i]:
            return False
    return True

