import sys

def main():
    expression = sys.stdin.readline().strip()
    result = 0
    for c in expression:
        if c == '+':
            result += 1
        elif c == '-':
            result -= 1
    print(result)

if __name__ == '__main__':
    main()

import re

def main():
    input = raw_input()
    print(re.sub(r'(\d+)', r'0\1', input))

if __name__ == '__main__':
    main()

import sys

def main():
    expr = sys.stdin.readline().strip()
    expr_list = expr.split('+')
    result = 0
    for num in expr_list:
        num_list = num.split('-')
        for n in num_list:
            result += int(n)
    print(result)

if __name__ == '__main__':
    main()

def main():
    expr = input()
    result = 0
    for i in range(len(expr)):
        if expr[i] == '+':
            result += int(expr[i+1])
        elif expr[i] == '-':
            result -= int(expr[i+1])
    print(result)

if __name__ == '__main__':
    main()
