def main():
    """
    >>> main(3)
    2
    >>> main(10)
    3
    >>> main(100)
    9
    >>> main(1)
    0
    """
    N = int(input())  # 入力値
    r, c = 1, 1

    while r * c < N:  # 縦横のマス目を増やしていく
        if r < c:
            r += 1
        else:
            c += 1
    print(r + c - 2)


if __name__ == "__main__":
    main()
import sys

def main():
    n = int(sys.stdin.readline())
    move = 0
    i = 1
    j = 1

    while i < n and j < n:
        if i == j:
            i += 1
        elif i < j:
            i += 1
        elif i > j:
            j += 1

        move += 1

    print(move)

