from random import randint
from itertools import combinations
from math import ceil
from collections import defaultdict

def generate_random_array(n):
    return [randint(1, 100) for _ in range(n)]

def generate_random_d(a):
    return randint(1, min(a))

def generate_random_sequence(n):
    a = generate_random_array(n)
    d = generate_random_d(a)
    return a, d

def generate_test_case(n):
    a, d = generate_random_sequence(n)
    print(n)
    print(' '.join(map(str, a)))
    print(d)

def read_array():
    return list(map(int, input().split()))

def is_possible(a, d):
    if d == 0:
        return len(set(a)) == 1
    return any(sum(a) % len(a) == 0 for a in combinations(a, d))

def solve(a):
    n = len(a)
    d = 0
    while d < min(a) and not is_possible(a, d):
        d += 1
    if d == min(a) + 1:
        return -1
    return d

def main():
    n = int(input())
    a = read_array()
    ans = solve(a)
    print(ans)

