def solve(n, a):
    s = sum(a)
    if s % n != 0:
        return -1
    ave = s // n
    return min(max(a[i] - ave, ave - a[i]) for i in range(n))

n = int(input())
a = list(map(int, input().split()))
print(solve(n, a))
