def solve(n, k, a):
    if n < k:
        return False
    c = [0] * n
    for i in range(n):
        c[i] = min(k, a[i])
        for j in range(i):
            if a[i] == a[j]:
                c[i] = c[j]
                break
        k -= c[i]
    return k == 0

def main():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    if not solve(n, k, a):
        print("NO")
        return
    print("YES")
    c = [0] * n
    for i in range(n):
        c[i] = min(k, a[i])
        for j in range(i):
            if a[i] == a[j]:
                c[i] = c[j]
                break
        k -= c[i]
    print(*c)

if __name__ == '__main__':
    main()

