import sys

def main():
    n = int(sys.stdin.readline().strip())
    a = 0
    while n > 0:
        if n % 2 == 1:
            a += 1
        n //= 2
    print(a)


if __name__ == '__main__':
    main()

a = int(input())

def fib(n):
	if n == 1 or n == 2:
		return 1
	else:
		return fib(n-1) + fib(n-2)

def find_fib(n):
	i = 1
	while fib(i) <= n:
		i += 1
	return i-1

