import sys

sys.setrecursionlimit(10 ** 6)


def read_list_int():
    return list(map(int, sys.stdin.readline().strip().split(' ')))


def read_single_int():
    return int(sys.stdin.readline().strip())


def solve(n, d, k):
    if k + 1 < d:
        return 'NO'

    if k == 1:
        if d == 1:
            return 'YES'
        else:
            return 'NO'

    if d == 1:
        return 'YES\n' + '\n'.join(['%d %d' % (i + 2, 1) for i in range(n - 1)])

    if d == 2:
        if k == 2:
            return 'NO'
        else:
            return 'YES\n' + '\n'.join(['%d %d' % (i + 2, 1) for i in range(n - 1)])

    if d == 3:
        if k == 3:
            return 'NO'
        else:
            return 'YES\n' + '\n'.join(['%d %d' % (i + 2, 1) for i in range(n - 1)])

    if d == 4:
        if k == 4:
            return 'NO'
        else:
            return 'YES\n' + '\n'.join(['%d %d' % (i + 2, 1) for i in range(n - 1)])


def main():
    n, d, k = read_list_int()
    print(solve(n, d, k))


if __name__ == '__main__':
    main()

