def solve(a, b, x):
    s = "0" * a + "1" * b
    for i in range(x):
        if i % 2 == 0:
            s = s[1:] + s[0]
        else:
            s = s[-1] + s[:-1]
    return s

def main():
    a, b, x = map(int, input().split())
    print(solve(a, b, x))

if __name__ == '__main__':
    main()

