import sys

def main():
    a,b,x=map(int,sys.stdin.readline().split())
    if(x%2==0):
        for i in range(x//2):
            sys.stdout.write('10')
        for i in range(a-x//2):
            sys.stdout.write('0')
        for i in range(b-x//2):
            sys.stdout.write('1')
    else:
        for i in range(x//2+1):
            sys.stdout.write('10')
        for i in range(a-x//2-1):
            sys.stdout.write('0')
        for i in range(b-x//2):
            sys.stdout.write('1')
    sys.stdout.write('\n')

if __name__ == '__main__':
    main()
