def main():
    n = int(input())
    q = list(map(int, input().split()))
    p = [0] * n
    p[0] = q[0]
    for i in range(1, len(q)):
        p[i] = q[i] + p[i-1]
    if max(p) > n or min(p) < 1:
        print(-1)
    else:
        for i in range(len(p)):
            p[i] += abs(min(p)) + 1
        print(*p)

