import sys

N = int(sys.stdin.readline())

def reach_N(N):
    if N == 1:
        return 0
    else:
        i = 1
        while i * i < N:
            i += 1
        if i * i == N:
            return i - 1
        else:
            return i + reach_N(N - i * (i - 1)) - 1

print(reach_N(N))

