def solve(s):
    zeros = s.count('0')
    ones = s.count('1')
    twos = s.count('2')
    if zeros > ones:
        return s.replace('2', '1', zeros - ones)
    elif ones > zeros:
        return s.replace('1', '0', ones - zeros)
    else:
        return s

