def is_prime(n):
    if n == 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True


def main():
    num = int(input())
    if is_prime(num):
        print(1)
    else:
        print(0)


if __name__ == '__main__':
    main()

