def is_diverse(s):
    if len(s) == 1:
        return True

    chars = set(s)
    if len(chars) != len(s):
        return False

    if ord(s[0]) + len(s) - 1 != ord(s[-1]):
        return False

    for i in range(1, len(s)):
        if ord(s[i]) != ord(s[i - 1]) + 1:
            return False

    return True

def main():
    n = int(input())
    for _ in range(n):
        s = input()
        if is_diverse(s):
            print('Yes')
        else:
            print('No')

if __name__ == '__main__':
    main()

