N = int(input())

def get_name(n):
    def get_length(n):
        length = 0
        while True:
            length += 1
            if n <= 26:
                return length
            n -= 26
            n //= 26

    def get_position(n):
        position = 0
        length = get_length(n)
        for l in range(1, length):
            position += 26 ** l
        position += n - 26 ** (length - 1)
        return position

    def get_char(n):
        return chr(96 + n)

    name = ""
    length = get_length(N)
    position = get_position(N)
    for l in range(length):
        if l == 0:
            n = position
        else:
            n = position // (26 ** (length - l - 1))
            position -= n * (26 ** (length - l - 1))
        name += get_char(n)
    return name

