def is_lucky(num):
    """
    Function to check if the number is a lucky number
    """
    # Split the number into two halfs
    half = len(num) // 2
    # Check if the first half and second half sum is equal
    return sum(map(int, num[:half])) == sum(map(int, num[half:]))

def main():
    """
    Main Function
    """
    num = input()
    if is_lucky(num):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()

