import sys

def is_lucky(string):
    length = len(string)
    half_length = length // 2
    if sum(int(string[i]) for i in range(half_length)) == sum(int(string[i]) for i in range(half_length, length)):
        return "Yes"
    else:
        return "No"

def main():
    string = sys.stdin.readline().strip()
    print(is_lucky(string))

if __name__ == "__main__":
    main()
