def solve(n, a):
    if n == 1:
        return True
    if a[0] != a[1]:
        return False
    if a[-1] != a[-2]:
        return False
    for i in range(1, n - 1):
        if a[i] != a[i - 1] and a[i] != a[i + 1]:
            return False
    return True


def main():
    n = int(input())
    a = list(map(int, input().split()))
    print('YES' if solve(n, a) else 'NO')


