n, x = map(int, input().split())
city = list(map(int, input().split()))
city.append(x)
city.sort()

for i in range(len(city)):
    if city[i] == x:
        x_index = i

if x_index == 0:
    print(city[1]-city[0])
elif x_index == len(city)-1:
    print(city[-1]-city[-2])
else:
    print(max(city[x_index]-city[x_index-1], city[x_index+1]-city[x_index]))

import sys

def main():
    n, x = list(map(int, sys.stdin.readline().split()))
    xs = list(map(int, sys.stdin.readline().split()))
    xs.append(x)
    xs.sort()
    d = 1
    for i in range(n):
        if xs[i+1] - xs[i] > d:
            d = xs[i+1] - xs[i]
    print(d)

if __name__ == '__main__':
    main()
