    t = input()
    s = [''] * n
    for i in range(n - 1, -1, -1):
        if i + 1 < n:
            s[i] = t[i + 1]
        else:
            s[i] = t[i]
        for j in range(i + 1, n, i + 1):
            s[j] = t[j - 1]
