def get_digit(k):
    if k == 1:
        return 1
    else:
        # get kth digit by block
        block_size = math.floor(math.sqrt(2*k))
        if block_size * (block_size + 1) / 2 < k:
            block_size += 1
        block_start = (block_size - 1) * block_size / 2 + 1
        block_end = block_size * block_size / 2
        digit_index = k - block_start
        digit = math.floor(digit_index / block_size) + 1
        if digit_index % block_size == 0:
            digit -= 1
        return int(digit)

