import math

def get_digit(k):
    if k == 1:
        return 1
    else:
        n = int(math.log10(k)) + 1
        first_number = 10 ** (n - 1)
        # print(n, first_number)
        first_number_start = (first_number - 1) * n + 1
        # print(first_number_start)
        if k >= first_number_start:
            diff = k - first_number_start
            return int(str(first_number + diff // n)[diff % n])
        else:
            return get_digit(k - (first_number - 1) * n)

