def main():
    q = int(sys.stdin.readline())
    for _ in range(q):
        k = int(sys.stdin.readline())
        print(solve(k))


def solve(k):
    # Find the block number
    block = 1
    while k - block > 0:
        k -= block
        block += 1

    # Find the digit in the block
    return str((block - 1) * block // 2 + k)[-1]


