    # dp[i] is the number of ways to get to i, so we want dp[0]
    dp = [0] * (w+1)
    dp[0] = 1

    for i in range(n):
        new_dp = [0] * (w+1)
        for j in range(w+1):
            if dp[j]:
                if 0 <= j + seq[i] <= w:
                    new_dp[j+seq[i]] += dp[j]
                if 0 <= j - seq[i] <= w:
                    new_dp[j-seq[i]] += dp[j]
        dp = new_dp

