def main():
    n = int(input())
    nums = list(map(int, input().split()))
    nums.sort()
    ans = []
    while len(nums) > 1:
        ans.append(nums.pop())
        ans.append(nums.pop(0))
    if len(nums) > 0:
        ans.append(nums.pop())
    print(' '.join(map(str, ans)))

if __name__ == "__main__":
    main()
