def main():
    N = int(input())
    if N == 0:
        print(0)
    else:
        ans = ''
        while N != 0:
            if N % 2 == 0:
                ans += '0'
                N //= -2
            else:
                ans += '1'
                N = (N - 1) // -2
        print(ans[::-1])

if __name__ == '__main__':
    main()

import sys

def to_base_2(n):
    if n == 0:
        return "0"
    elif n == 1:
        return "1"
    elif n % 2 == 0:
        return to_base_2(n // -2) + "0"
    else:
        return to_base_2((n - 1) // -2) + "1"


n = int(sys.stdin.readline())
print(to_base_2(n))

