import sys

N = int(sys.stdin.readline())

if N == 0:
    print(0)
else:
    result = []
    while N != 0:
        if N % 2 == 0:
            result.append(0)
            N = N // (-2)
        else:
            result.append(1)
            N = (N - 1) // (-2)
    print("".join(map(str, reversed(result))))

# nを-2進数で表示する
def base_minus2(n):
    # nが0の場合
    if n == 0:
        return "0"
    # nが負の場合
    if n < 0:
        res = "1"
        n = -n
    # nが正の場合
    else:
        res = ""
    # nが0になるまで余りを計算する
    while n != 0:
        res = str(n % 2) + res
        n = (n - n % 2) // -2
    return res

def main():
    n = int(input())
    print(base_minus2(n))

if __name__ == '__main__':
    main()
