# X = 100

A, B, X = map(int, input().split())

max_num = 10**9

def price(n):
    return A*n + B*len(str(n))

def check(n):
    if n > max_num:
        return False
    else:
        return True

def binary_search(l, r):
    if l == r:
        return l
    else:
        m = int((l + r)/2)
        if price(m) <= X and check(m+1) and price(m+1) > X:
            return m
        elif price(m) <= X and check(m+1):
            return binary_search(m+1, r)
        else:
            return binary_search(l, m)

if price(max_num) <= X:
    print(max_num)
else:
    print(binary_search(0, max_num))

