import sys, math

def main():
    a, b, x = map(int, sys.stdin.readline().split())
    if x < a:
        return 0
    if a + b > x:
        return 1
    num = (x - a) / b
    if num < 10:
        return int(num)
    else:
        num = math.floor(math.log10(num)) + 1
        return int((x - a - b * num) / a)

if __name__ == "__main__":
    print(main())
