#----input----
S = list(input())
K = int(input())

#----main----
def main(S, K):
    count = [0] * 10
    # count[i] : 数字iがSに含まれる個数
    for s in S:
        count[int(s)] += 1

    # 各数字を桁数が増える前と増えた後で計算する
    before = [0] * 10
    after = [0] * 10
    for i in range(1, 10):
        before[i] = count[i]
        after[i] = count[i] * (i+1)

    # 各数字が増える前と増えた後で、各桁数がKを超えるまで計算する
    while sum(after) < K:
        for i in range(1, 10):
            before[i] = after[i]
            after[i] = before[i] * (i+1)

    # 各数字がKを超えるまで計算した中で、Kを超えている数字をリストに格納
    over = []
    for i in range(1, 10):
        if before[i] >= K:
            over.append(i)

    # 各数字がKを超えている場合、その数字を返す
    if len(over) == 1:
        return over[0]

    # 各数字がKを超えている場合、その数字の中でKを超える数字を返す
    else:
        for i in range(1, 10):
            if before[i] < K:
                K -= before[i]
            else:
                return i

result = main(S, K)
print(result)

