def solve(n, k, a):
    d = [0] * (k + 1)
    for i in range(n):
        d[a[i] % k] += 1
    ans = 0
    for i in range(k + 1):
        ans += d[i] * (d[i] - 1) // 2
    for i in range(k + 1):
        if d[i] > 0:
            ans += d[i] * d[(k - i) % k]
    return ans


