from itertools import permutations

N = int(input())
B = list(map(int, input().split()))

max_sum = 0

for perm in permutations(range(1, N+1)):
    sum = 0
    for i in range(N-1):
        if B[i] >= max(perm[i], perm[i+1]):
            sum += perm[i]
        else:
            break
    else:
        sum += perm[N-1]
        if sum > max_sum:
            max_sum = sum

print(max_sum)

def solve(N, Bs):
    max_sum = 0
    for i in range(N):
        max_sum += Bs[i]
    for i in range(N-1):
        if Bs[i] < Bs[i+1]:
            max_sum -= Bs[i]
        else:
            max_sum -= Bs[i+1]
    return max_sum


if __name__ == '__main__':
    N = int(input())
    Bs = list(map(int, input().split()))
    print(solve(N, Bs))
