N = int(input())

# 1 ~ N までの FizzBuzz の値を計算する
fizzbuzz_list = []
for i in range(1, N + 1):
    if i % 15 == 0:
        fizzbuzz_list.append("FizzBuzz")
    elif i % 3 == 0:
        fizzbuzz_list.append("Fizz")
    elif i % 5 == 0:
        fizzbuzz_list.append("Buzz")
    else:
        fizzbuzz_list.append(i)

# FizzBuzz の値を足し合わせる
result = 0
for i in range(len(fizzbuzz_list)):
    if type(fizzbuzz_list[i]) == int:
        result += fizzbuzz_list[i]

