def is_good(s):
    for i in range(0, len(s), 2):
        if s[i] == s[i+1]:
            return False
    return True


def remove_char(s):
    for i in range(0, len(s), 2):
        if s[i] == s[i+1]:
            return s[:i]+s[i+2:]
    return s


def solve(s):
    if is_good(s):
        return s
    else:
        return solve(remove_char(s))


if __name__ == "__main__":
    n = int(input())
    s = input()
    print(len(s)-len(solve(s)))
    print(solve(s))

