import sys

def main():
    n = int(input())
    s = input()

    # Check if the string is already good
    good = True
    for i in range(0, len(s), 2):
        if i + 1 < len(s) and s[i] == s[i + 1]:
            good = False
            break

    if good:
        print(0)
        print(s)
        return

    # Remove the extra characters
    res = []
    for i in range(0, len(s), 2):
        if i + 1 < len(s) and s[i] == s[i + 1]:
            if i + 2 < len(s):
                res.append(s[i + 2])
        else:
            res.append(s[i])

    print(len(s) - len(res))
    print(''.join(res))

