import math

def calc_num_of_op(n):
    num_of_op = 0
    for i in range(1, int(math.sqrt(n))+1):
        if n % i == 0:
            num_of_op += 2
    if math.sqrt(n) == int(math.sqrt(n)):
        num_of_op -= 1
    return num_of_op

def main():
    n = int(input())
    num_of_op = calc_num_of_op(n)
    print(num_of_op)

if __name__ == '__main__':
    main()

