import sys

def main():
    n = int(sys.stdin.readline())
    x = 0
    while n > 0:
        x += 1
        if n % 6 == 0:
            n //= 6
        elif n % 9 == 0:
            n //= 9
        else:
            n -= 1
    print(x)

