from collections import defaultdict
n = int(input())
a = list(map(int, input().split()))

d = defaultdict(int)
for i in range(n):
    d[a[i]] += 1

ans = 0
for i in range(n):
    for j in range(i+1, n):
        if abs(a[i] - a[j]) <= 5:
            ans = max(ans, d[a[i]] + d[a[j]])
print(ans)

def solve(n, a):
    a.sort()
    max_size = 1
    for i in range(n):
        l, r = i, i + 1
        while l >= 0 and r < n and a[r] - a[l] <= 5:
            max_size = max(max_size, r - l + 1)
            l -= 1
            r += 1
    return max_size


def main():
    n = int(input())
    a = list(map(int, input().split()))
    print(solve(n, a))


if __name__ == '__main__':
    main()

