def main():
    n = int(input())
    nums = [int(x) for x in input().split()]
    for i in range(n):
        if nums[i] % 2 == 0:
            nums[i] -= 1
        else:
            nums[i] += 1
    print(*nums)

if __name__ == '__main__':
    main()

