from sys import stdin
from collections import deque

def main():
    n, k = map(int, stdin.readline().strip().split())
    s = stdin.readline().strip()
    d = deque(s)
    for _ in range(k):
        for c in "abcdefghijklmnopqrstuvwxyz":
            if c in d:
                d.remove(c)
                break
    print(''.join(d))

if __name__ == "__main__":
    main()
