import math

def main():
    k = int(input())
    if k % 2 == 0:
        print(int(math.pow(k/2, 2)))
    else:
        print(int(math.pow((k-1)/2, 2) + math.pow((k-1)/2, 2)))

if __name__ == '__main__':
    main()
